$('#acuerdo').click(function() {
    document.getElementById("contenido").style.display = "block";
    document.getElementById("condicion1").style.display = "none";
});

$('#contacto3show').click(function() {
    var element = document.getElementById("borde-rojo")
    element.classList.add("mp-bg-red");
    element.classList.add("uk-margin-small-top");
    document.getElementById("show").style.display = "none";
    document.getElementById("hide").style.display = "block";
    document.getElementById("polizas").style.display = "block";
});

$('#contacto3hide').click(function() {
    var element = document.getElementById("borde-rojo")
    element.classList.remove("mp-bg-red");
    element.classList.remove("uk-margin-small-top");
    document.getElementById("show").style.display = "block";
    document.getElementById("hide").style.display = "none";
    document.getElementById("polizas").style.display = "none";
});
